import { TemplateRef } from '@angular/core';
import { KeyValueObject } from './common.models';
export interface MentionConfig {
    /**
     * Items list. Can be string[] or object array of any type
     */
    items: (string | KeyValueObject)[];
    /**
     * Specifies the character to cause the mention autocomplete to open
     */
    triggerChar: string;
    /**
     * Object key to run searching on items list
     */
    searchKey?: string;
    /**
     * Custom item template
     */
    itemTemplate?: TemplateRef<string | KeyValueObject>;
    /**
     * Custom template on top
     */
    topSideTemplate?: TemplateRef<(string | KeyValueObject)[]>;
}
