export interface MenuItem {
    label: string;
    name?: string;
    link?: string;
    target?: string;
    routerLink?: any;
    route?: any;
    action?: (event?: any, id?: string) => void;
    /**
     * If set to true, will exclude adding active class to the navigation item of type action
     * @since v12.9.0
     */
    excludeActiveOnAction?: boolean;
    closeOnClick?: boolean;
    id: string;
    active?: boolean;
    selected?: boolean;
    hide?: boolean;
    className?: string;
    listClassName?: string;
    items?: MenuItem[];
    subMenu?: MenuItem[];
    header?: string;
    expanded?: boolean;
    disabled?: boolean;
    separator?: boolean;
    parent?: MenuItem;
    filteringConfig?: MenuItemFilteringConfig;
    testID?: string;
}
export interface MenuItemFilteringConfig {
    enabled: boolean;
    placeholder?: string;
    resultsTitle?: string;
    title?: string;
    notFoundText?: string;
    btnClearText?: string;
}
export interface CascadeItem {
    id: string;
    subMenuItems?: {
        parentNode: any;
        componentRef: any;
        anchor: any;
    }[];
}
