import { Message } from 'primeng/api/message';
/**
 * ADAPT toast message
 * Extension of Primeng Message type
 * @since v10.14.0
 */
export interface AdaptMessage extends Message {
    link?: string;
    linkHandler?: (_: AdaptMessage) => void;
}
/**
 * @since 11.3.0
 */
export interface AdaptToastCloseEvent {
    index: number;
    message: AdaptMessage;
}
